export enum IconName {
  star = "star",
  notify = "notify",
  hangup = "hangup",
  dismiss = "dismiss",
  phone = "phone",
  dialpad = "dialpad",
  dialpadOpen = "dialpadOpen",
  sms = "sms",
}

const getIcon = (icon: IconName): IconInterface => {
  switch (icon) {
    case IconName.star:
      return star;
    case IconName.notify:
      return notify;
    case IconName.hangup:
      return hangup;
    case IconName.dismiss:
      return dismiss;
    case IconName.phone:
      return phone;
    case IconName.dialpad:
      return dialpad;
    case IconName.dialpadOpen:
      return dialpadOpen;
    case IconName.sms:
      return sms;
  }
};

export default getIcon;
